const $=document,bells=$.querySelectorAll(".bell"),closeBtn=$.querySelector(".close-icon"),message=$.querySelector(".message"),messageHeader=$.querySelector(".message-header"),textAria=$.querySelector("#textAria"),copy=$.querySelector(".copy");bells.forEach(bell=>{bell.addEventListener("click",()=>{message.style.transform="translateY(0)",messageHeader.innerHTML="Please click on copy button and paste it on your SASS/SCSS file.",textAria.innerHTML="",copy.addEventListener("click",()=>{textAria.innerHTML=`@include ${bell.firstElementChild.getAttribute("data-name")}`,textAria.select(),document.execCommand("copy")})})}),closeBtn.addEventListener("click",()=>{message.style.transform="translateY(-120vh)"});